import React, {Component} from 'react';
import { Image } from 'react-native';
import { StyleSheet, View, Alert, ActivityIndicator } from 'react-native';
import { Container, Content, Left, Right, Icon, Thumbnail, Card, CardItem, Body, Text, Button, Item } from 'native-base';

export default class Products extends Component{
    constructor(props){
        super(props);
        this.state ={
          products: [],
          isLoading: true,
          result: null
        };
    }
    componentDidMount = () => {
        fetch('https://api.github.com/repositories',{ method: 'GET'})
          .then(response => {              
              return response.json();
          })
          .then(response =>{
//              this.state.products = response;
              this.setState({
                  products: response,
                  isLoading: false
              },()=>{
                    console.log('seteo la variable');
                    console.log(this.state.products);
                    let res = this.state.products.map((item, i)=>{
                        console.log(item);
                        <Content>
                            <Card style={{flex: 0}}>
                                <CardItem>
                                    <Image source={{uri: 'https://automociontotal.hiperarticulos.com/wp-content/uploads/2016/07/baterías-de-automóviles-1.jpg'}} style={{height: 200, width: '100%', flex: 1}}/>             
                                </CardItem>
                                <CardItem>
                                    <Body>
                                        <Text>
                                        //Your text here
                                        </Text>
                                    </Body>
                                </CardItem>
                                <CardItem>
                                  <Left>
                                    <Button transparent textStyle={{color: '#87838B'}}>
                                      <Icon name="eye" />
                                      <Text>Ver más</Text>
                                    </Button>
                                  </Left>
                                  <Right>
                                    <Button transparent textStyle={{color: '#87838B'}}>
                                      <Icon name="cart" />
                                      <Text>Comprar</Text>
                                    </Button>
                                  </Right>
                                </CardItem>
                            </Card>
                        </Content>
                    });
                    this.setState({
                        result: res
                    },()=>{console.log('termino'); console.log(this.state.result)});
                }
              );
          })
          .catch((error) => {
              console.log(this.state.products);
            console.error(error,"ERRRRRORRR");
          });         
    }

    render(){        
        if(this.state.isloading){
            return(
                <View>
                    <ActivityIndicator size="large" color="red" />
                </View>
            )    
        }
        return(this.state.result)
    }
}
const styles = StyleSheet.create({
  bgGray: {
      backgroundColor: '#00148A',
  },
  textCenter: {
    textAlign: 'center',
    width: '100%',
    fontWeight: 'bold',
    fontSize: 19
  },
  content: {
      flex: 1,
      justifyContent: 'center',
  },
  bodyCard: {
      borderColor: 'transparent',
      borderStyle: 'dashed',
      borderBottomColor: 'transparent',
      borderTopColor: 'transparent',     
  },
  bgCardItem: {
      backgroundColor: 'rgba(255, 255, 255, 0.6)',
      borderColor: 'transparent',
      borderStyle: 'dashed',
      borderRadius: 0,      
  },
  bgCard: {
    backgroundColor: 'transparent',
    borderRadius: 3,
    height: 320,
    borderColor: 'transparent',
  },
  buttonRight: {
      left: '0%',
  },
  buttonLeft: {
      right: '0%',
      marginRight: 20,
      marginLeft: 20
  },
  container: {
    flex: 1,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
