import React, {Component} from 'react';
import { StyleSheet, View, Alert, Image, ActivityIndicator, AsyncStorage } from 'react-native';
import { Container, Content, Card, CardItem, Body, Text, Button, Form, Item, Label, Input, Icon, Right } from 'native-base';

async function getToCart(){    
        const value = AsyncStorage.getItem('@cart');            
        console.log(JSON.parse(value));
        this.setState({cart: JSON.parse(value), loader: false});
}  
export default class Cart extends Component {
    constructor(props){
        super(props);        
        this.state ={
          cart: false,
          loader: true
        };
    }
    async getToCart(){    
        if(!this.state.cart){
            const value = await AsyncStorage.getItem('@cart');
            this.setState({cart: JSON.parse(value), loader: false});
        }
    } 
    a(){
        console.log('aaaa');
    }
    render(){   
        this.getToCart();
        if(this.state.loader){
            return(
                <Container>
                    <ActivityIndicator size="large" color="red" />   
                </Container>      
            )    
        }
        console.log(this.state.cart);
        return(
            <Container>
                <Content>                 
                    <Card style={{flex: 0}}>
                        <CardItem>
                                  <Image source={{uri: 'https://noticiasbancarias.com/wp-content/plugins/accelerated-mobile-pages/images/SD-default-image.png'}} style={{height: 80, width: 80, flex: 1, resizeMode: 'cover', alignSelf: 'stretch'}}/>
                                  <Text>nombre</Text>

                                  <Right onPres={()=>this.this.a}>
                                    <Icon name="arrow-forward" />
                                    <Button onPres={()=>this.this.a}><Text>Comprar</Text></Button>
                                  </Right>
                                </CardItem>
                        {
                            this.state.cart.map((products,i) => (
                                <CardItem key={i}>
                                  <Image source={{uri: products.images[0].src}} style={{height: 80, width: 80, flex: 1, resizeMode: 'cover', alignSelf: 'stretch'}}/>
                                  <Text>{products.name}</Text>

                                  <Right onPres={this.a}>
                                    <Icon name="arrow-forward" />
                                    <Button onPres={()=>this.props.navigation.navigate('Detail',{product: products})}><Text>Comprar</Text></Button>
                                  </Right>
                                </CardItem>
                            ))  
                        }                          
                    </Card>                                         
                </Content>
            </Container>
        )
    }
}
