import React, {Component} from 'react';
import { StyleSheet, View, Alert, Image, ActivityIndicator, AsyncStorage } from 'react-native';
import { Container, Header, Content, Card, CardItem, Body, Text, Button, Form, Item, Label, Input, Icon, Right } from 'native-base';

export default class Cart extends Component {
    constructor(props){
        super(props);        
        this.state ={
          cart: false,
          isLoading: true
        };
    }
    async valToCart(){
        console.log('ssdfsfsfs');
        const value = await AsyncStorage.getItem('@cart');
        console.log(value);
        this.setState({
            cart: JSON.parse(value),
            isLoading: false
        })
    }
    render(){
        this.valToCart();
        if(this.state.isLoading){
            return(
                <Container>
                    <ActivityIndicator size="large" color="red" />   
                </Container>      
            )    
        }
        return(
            <Container>
                <Content>                 
                    <Card style={{flex: 0}}>
                        {
                            this.state.cart.map((products,i) => (
                                <CardItem  key={i} onPress={()=>this.props.navigation.navigate('Detail')}>
                                  <Image source={{uri: products.images[0].src}} style={{height: 50, width: '100%', flex: 1, resizeMode: 'contain', alignSelf: 'stretch'}}/>
                                  <Text>{products.name}</Text>
                                  <Right>
                                    <Icon name="arrow-forward" />
                                  </Right>
                                </CardItem>
                            ))  
                        }                          
                    </Card>                                         
                </Content>
            </Container>
        )
    }
}
const styles = StyleSheet.create({
  bgGray: {
      backgroundColor: '#00148A',
  },
});
