import React, {Component} from 'react';
import { StatusBar, StyleSheet, View, ImageBackground, Alert, ActivityIndicator, Image } from 'react-native';
import { Container, Header, Content, Card, CardItem, Body, Text, Button, Form, Item, Label, Input, Icon, Left, Right, Title, Picker, Spinner } from 'native-base';
import { LazyloadScrollView, LazyloadView, LazyloadImage, LazyloadListView } from 'react-native-lazyload-deux';
import {decode, encode} from 'base-64'

import HeaderDefault from '../components/Header';

export default class Home extends Component {
    constructor(props){
        super(props);        
        this.state ={
          products: [],
          categories: [],
          isLoading: true,
          selected2: undefined
        };
    }
    componentDidMount = () => {
        fetch('http://modablackshop.tk/wp-json/wc/v3/products/categories',{ 
            method: 'GET',
            headers: new Headers({
                'Authorization': 'Basic '+encode('administrador:12345678'), 
                'Content-Type': 'application/x-www-form-urlencoded'
            }), 
        })
          .then(response => {              
              return response.json();
          })
          .then(response =>{
              this.setState({
                  categories: response,
                  isLoading: false
              },()=>{
//                  console.log('seteo la variable');
                  console.log(this.state.categories);
                }
              );
          })
          .catch((error) => {
            console.error(error,"ERRRRRORRR");
          });
        fetch('http://modablackshop.tk/wp-json/wc/v3/products?per_page=100',{ 
            method: 'GET',
            headers: new Headers({
                'Authorization': 'Basic '+encode('administrador:12345678'), 
                'Content-Type': 'application/x-www-form-urlencoded'
            }), 
        })
          .then(response => {              
              return response.json();
          })
          .then(response =>{
              this.setState({
                  products: response,
                  isLoading: false
              },()=>{
//                  console.log('seteo la variable');
                  console.log(this.state.products);
                }
              );
          })
          .catch((error) => {
            console.error(error,"ERRRRRORRR");
          });         
    }
    onValueChange2(value) {
        this.setState({isLoading: true });
        console.log(this.state.isLoading);        
        fetch('http://modablackshop.tk/wp-json/wc/v3/products?category='+value,{ 
            method: 'GET',
            headers: new Headers({
                'Authorization': 'Basic '+encode('administrador:12345678'), 
                'Content-Type': 'application/x-www-form-urlencoded'
            }), 
        })
        .then(response => {              
              return response.json();
        })
        .then(response =>{
              this.setState({
                  products: response,
                  isLoading: false,
                  selected2: value
              },()=>{
//                  console.log('seteo la variable');
                  console.log(this.state.products);
                }
              );
        })
        .catch((error) => {
            console.error(error,"ERRRRRORRR");
        });
      }
    render(){
        console.log(this.state.isLoading)
        if(this.state.isLoading){
            return(
                <Container>
                    <HeaderDefault />
                    <ActivityIndicator size="large" color="red" />   
                </Container>      
            )    
        }
        const {navigate} = this.props.navigation;
        let result = 
              <Container>                
                <HeaderDefault />
                    <Form>
                        <Item picker>
                            <Picker
                              style={{ width: undefined }}
                              selectedValue={this.state.selected2}
                              onValueChange={this.onValueChange2.bind(this)}
                            >
                            {
                                this.state.categories.map((categories,i) => (
                                    <Picker.Item label={categories.name} value={categories.id} />
                                ))
                            }
                            </Picker>
                        </Item>
                    </Form>
                <Content>
                <LazyloadScrollView name="unique-lazyload-list-name">
                 {
                    this.state.products.map((products,i) => (                    
                        <Card style={{flex: 0}} key={i} >
                            <CardItem>
                            <LazyloadView host="unique-lazyload-list-name" key={i} >
                                <LazyloadImage host="unique-lazyload-list-name" source={{uri: products.images[0].src}} />
                            </LazyloadView>                
                            </CardItem>
                            <CardItem>
                                <Body>
                                    <Title>{products.name}</Title>
                                    <Text>
                                    {products.name}
                                    </Text>
                                </Body>
                            </CardItem>
                            <CardItem>
                              <Left>
                                <Button transparent textStyle={{color: '#87838B'}}>
                                  <Icon name="eye" />
                                  <Text>Ver más</Text>
                                </Button>
                              </Left>
                              <Right>
                                <Button transparent textStyle={{color: '#87838B'}} onPress={()=>this.props.navigation.navigate('Login')}>
                                  <Icon name="cart" />
                                  <Text>Comprar</Text>
                                </Button>
                              </Right>
                            </CardItem>
                        </Card>                    
                    ))
                 }  
                 </LazyloadScrollView>
                </Content>
              </Container>              
        return(result);
    }
}
const styles = StyleSheet.create({
  bgGray: {
      backgroundColor: '#00148A',
  },
  textCenter: {
    textAlign: 'center',
    width: '100%',
    fontWeight: 'bold',
    fontSize: 19
  },
  content: {
      flex: 1,
      justifyContent: 'center',
  },
  bodyCard: {
      borderColor: 'transparent',
      borderStyle: 'dashed',
      borderBottomColor: 'transparent',
      borderTopColor: 'transparent',     
  },
  bgCardItem: {
      backgroundColor: 'rgba(255, 255, 255, 0.6)',
      borderColor: 'transparent',
      borderStyle: 'dashed',
      borderRadius: 0,      
  },
  bgCard: {
    backgroundColor: 'transparent',
    borderRadius: 3,
    height: 320,
    borderColor: 'transparent',
  },
  buttonRight: {
      left: '0%',
  },
  buttonLeft: {
      right: '0%',
      marginRight: 20,
      marginLeft: 20
  },
  container: {
    flex: 1,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
