import React, {Component} from 'react';
import { StyleSheet, View, Alert, Image, ActivityIndicator, AsyncStorage } from 'react-native';
import { Container, Content, Card, CardItem, Body, Text, Button, Form, Item, Label, Input, Icon, Left, Right, Title } from 'native-base';

export default class Detail extends Component {
    constructor(props){
        super(props);   
        console.log(props)
        this.state ={
          cart: false,
          loader: true
        };
    }
    render(){
        const params = this.props.navigation.state.params.product;
        console.log(params);
        let result = 
              <Container>
                <Content>
                 {
                    <Card style={{flex: 0}}>
                        <CardItem cardBody>
                            <Image source={params.images[0].src?{uri: params.images[0].src}:{uri: 'https://noticiasbancarias.com/wp-content/plugins/accelerated-mobile-pages/images/SD-default-image.png'}} style={{height: 200, width: '100%', flex: 1, resizeMode: 'contain', alignSelf: 'stretch'}}/>             
                        </CardItem>
                        <CardItem>
                            <Body>                                
                                <Text style={{fontWeith: 'bold', fontSize: 28, textTransform: 'uppercase'}}>
                                {params.name}
                                </Text>
                                <Text>
                                {params.short_description.replace(/<[^>]*>?/g, '')}
                                </Text>
                            </Body>
                        </CardItem>
                        <CardItem>
                          <Left>
                            <Button transparent textStyle={{color: '#87838B'}}>
                              <Icon name="eye" />
                              <Text>Ver más</Text>
                            </Button>
                          </Left>
                          <Right>
                            <Button transparent textStyle={{color: '#87838B'}}>
                              <Icon name="cart" />
                              <Text>Comprar</Text>
                            </Button>
                          </Right>
                        </CardItem>
                    </Card>
                 }        
                </Content>
              </Container>              
        return(result);
    }
}
const styles = StyleSheet.create({
  bgGray: {
      backgroundColor: '#00148A',
  },
});
