import React, {Component} from 'react';
import { Image } from 'react-native';
import { StyleSheet, View, Alert, ActivityIndicator } from 'react-native';
import { Container, Content, Left, Right, Icon, Thumbnail, Card, CardItem, Body, Text, Button, Item, Title } from 'native-base';

function a(){
    Alert.alert('hola mundo');
}

export default class Products extends Component{
    constructor(props){
        super(props);
        this.state ={
          products: [],
          isLoading: true
        };
    }    
    componentDidMount = () => {
        fetch('http://modablackshop.tk/wp-json/wc/v3/products',{ 
            method: 'GET',
            headers: new Headers({
                'Authorization': 'Basic '+btoa('administrador:12345678'), 
                'Content-Type': 'application/x-www-form-urlencoded'
            }), 
        })
          .then(response => {              
              return response.json();
          })
          .then(response =>{
              this.setState({
                  products: response,
                  isLoading: false
              },()=>{
//                  console.log('seteo la variable');
                  console.log(this.state.products);
                }
              );
          })
          .catch((error) => {
            console.error(error,"ERRRRRORRR");
          });         
    }
    render(){
        addToCart = ()=>{
            Alert.alert('hola mundo');
        }
        if(this.state.isloading){
            return(
                <View>
                    <ActivityIndicator size="large" color="red" />
                </View>
            )    
        }

        return(            
            <Content>
                 {
                    this.state.products.map((products,i) => (

                    <Card style={{flex: 0}} key={i}>
                            <CardItem>
                                <Image source={{uri: products.images[0].src}} style={{height: 200, width: '100%', flex: 1}}/>             
                            </CardItem>
                            <CardItem>
                                <Body>
                                    <Title>{products.name}</Title>
                                    <Text>
                                    {products.name}
                                    </Text>
                                </Body>
                            </CardItem>
                            <CardItem>
                              <Left>
                                <Button transparent textStyle={{color: '#87838B'}}>
                                  <Icon name="eye" />
                                  <Text>Ver más</Text>
                                </Button>
                              </Left>
                              <Right>
                                <Button transparent textStyle={{color: '#87838B'}} onPress={this.addToCart}>
                                  <Icon name="cart" />
                                  <Text>Comprar</Text>
                                </Button>
                              </Right>
                            </CardItem>
                        </Card>
                    ))
                }        
            </Content>
        )
    }
}
const styles = StyleSheet.create({
  bgGray: {
      backgroundColor: '#00148A',
  },
  textCenter: {
    textAlign: 'center',
    width: '100%',
    fontWeight: 'bold',
    fontSize: 19
  },
  content: {
      flex: 1,
      justifyContent: 'center',
  },
  bodyCard: {
      borderColor: 'transparent',
      borderStyle: 'dashed',
      borderBottomColor: 'transparent',
      borderTopColor: 'transparent',     
  },
  bgCardItem: {
      backgroundColor: 'rgba(255, 255, 255, 0.6)',
      borderColor: 'transparent',
      borderStyle: 'dashed',
      borderRadius: 0,      
  },
  bgCard: {
    backgroundColor: 'transparent',
    borderRadius: 3,
    height: 320,
    borderColor: 'transparent',
  },
  buttonRight: {
      left: '0%',
  },
  buttonLeft: {
      right: '0%',
      marginRight: 20,
      marginLeft: 20
  },
  container: {
    flex: 1,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
