import React, {Component} from 'react';
import { StyleSheet, View, Alert, Image, ActivityIndicator, AsyncStorage } from 'react-native';
import { Container, Content, Card, CardItem, Body, Text, Button, Form, Item, Label, Input, Icon, Right } from 'native-base';

export default class Cart extends Component {
    constructor(props){
        super(props);        
        this.state ={
          cart: null,
          loader: true
        };
    }
    async getToCart(){
        const value = AsyncStorage.getItem('@cart');            
        console.log(JSON.parse(value));
        this.setState({cart: JSON.parse(value), loader: false});
    }    
    render(){   
        getTocart();
        if(this.state.loader){
            return(
                <Container>
                    <ActivityIndicator size="large" color="red" />   
                </Container>      
            )    
        }
        console.log(this.state.cart);
        return(
            <Container>
                <Content>                 
                    <Card style={{flex: 0}}>
                        {
                            this.state.cart.map((products,i) => (
                                <CardItem  key={i}>
                                  <Image source={{uri: products.images[0].src}} style={{height: 50, width: '100%', flex: 1, resizeMode: 'contain', alignSelf: 'stretch'}}/>
                                  <Text>{products.name}</Text>
                                  <Right>
                                    <Icon name="arrow-forward" />
                                  </Right>
                                </CardItem>
                            ))  
                        }                          
                    </Card>                                         
                </Content>
            </Container>
        )
    }
}
